      out.write("				.trim();\r\n");
      out.write("		const phone = document.getElementById('phone').value.trim();\r\n");
      out.write("		const addr = document.getElementById('addr').value.trim();\r\n");
      out.write("\r\n");
      out.write("		// 유효성 검사\r\n");
      out.write("		if (!validateEmail(email)) {\r\n");
      out.write("			alert('유효한 이메일 주소를 입력하세요.');\r\n");
      out.write("			return;\r\n");
      out.write("		}\r\n");
      out.write("\r\n");
      out.write("		if (name === '') {\r\n");
      out.write("			alert('이름을 입력하세요.');\r\n");
      out.write("			return;\r\n");
      out.write("		}\r\n");
      out.write("\r\n");
      out.write("		if (password.length < 8) {\r\n");
