      out.write("		if (password !== passwordConfirm) {\r\n");
      out.write("			alert(\"비밀번호가 일치하지 않습니다.\");\r\n");
      out.write("			document.getElementById('password').value = ''; // 비밀번호 입력란 값 비우기\r\n");
      out.write("			document.getElementById('passwordConfirm').value = ''; // 비밀번호 확인란 값 비우기\r\n");
      out.write("			return; \r\n");
      out.write("		}\r\n");
      out.write("/* 		if (!validatePhone(phone)) {\r\n");
      out.write("			alert('유효한 휴대폰 번호를 입력하세요.');\r\n");
      out.write("			return;\r\n");
      out.write("		} */\r\n");
      out.write("\r\n");
      out.write("		if (addr === '') {\r\n");
